document.addEventListener("DOMContentLoaded", function() {
    var currentDate = new Date().toISOString().split('T')[0];
    var dob = document.getElementById("dob");
    dob.setAttribute("max", currentDate);
});


document.addEventListener("DOMContentLoaded", function() {
    var currentDate = new Date().toISOString().split('T')[0];
    var from_field = document.getElementById("from_field");
    var to_field = document.getElementById("to_field");
    from_field.setAttribute("max", currentDate);
    to_field.setAttribute("max", currentDate);
});
function GetDays(){
     var date1 = new Date(document.getElementById("from_field").value);
     var date2 = new Date(document.getElementById("to_field").value);
     var Difference_In_Time = date2.getTime() - date1.getTime();
     var Difference_In_Days = Math.ceil(Difference_In_Time / (1000 * 3600 * 24));
     var years = Math.floor(Difference_In_Days / 365);
     var months = Math.floor(Difference_In_Days % 365 / 30);
     var days = Math.floor(Difference_In_Days % 365 % 30);
     return years + "y" +" "+ months + "m" +" "+ days + "d";    
}
function caller(){
    if(document.getElementById("to_field")){
    document.getElementById("years").value=GetDays();
        }  
    }
